mapper.select:
		SELECT N1.A1CHUUBUNSHONO,N1.A1MITSUMORISHONO,N1.A1CHUUMONHIDZUKE,N1.A1SHOUHINSOUGAKUJPY,N1.A1SHANAIKOUJIBANGOU,N1.A1KYAKUSAKIID,N1.A1SHUTSUNYUUKOFURAGU,N1.A1TANTOUSHAID, '' nullAlsa0, '' nullAlsa1,N1.A1KYAKUSAKITANTOU, '' nullAlsa2 FROM (SELECT A1.CHUUBUNSHONO AS A1CHUUBUNSHONO ,A1.MITSUMORISHONO A1MITSUMORISHONO ,A1.CHUUMONHIDZUKE A1CHUUMONHIDZUKE ,A1.SHOUHINSOUGAKUJPY A1SHOUHINSOUGAKUJPY ,A1.SHANAIKOUJIBANGOU A1SHANAIKOUJIBANGOU ,A1.KYAKUSAKITANTOU A1KYAKUSAKITANTOU ,A1.SHUTSUNYUUKOFURAGU A1SHUTSUNYUUKOFURAGU ,A1.KYAKUSAKIID A1KYAKUSAKIID ,A1.TANTOUSHAID A1TANTOUSHAID FROM `TYUMONSYO` A1 WHERE 1=1 )N11=1 

AND   N1.A1CHUUMONHIDZUKE  <=   #{_} 


AND   N1.A1CHUUMONHIDZUKE  >=   #{CHUUMONBI} 


AND   N1.A1CHUUBUNSHONO LIKE CONCAT("%",#{CHUUMONNO},"%") 


AND   N1.A1SHANAIKOUJIBANGOU LIKE CONCAT("%",#{KOUJIBANGOU},"%") 


AND   N1.A1KYAKUSAKIID =  #{KYAKUSAKIID} 


AND   N1.A1MITSUMORISHONO LIKE CONCAT("%",#{MITSUMORINO},"%") 


AND   N1.A1TANTOUSHAID =  #{TANTOUSHAID} 


AND   N1.A1SHUTSUNYUUKOFURAGU =  #{SHUKKOJOUTAI} 
 ORDER BY  N1.A1CHUUBUNSHONO desc  LIMIT 50
