mapper.select:
		SELECT N1.A1SEISAKUHINKUBUN,N1.A1HINBANNO1,N1.A1HINMEI,N1.A1KOSUU,N1.A1SOUKOSUU,N1.A1TSUKAMATSUNYUUSAKI,N1.A1KIBOUNOUKI,N1.A1TANTOU,N1.A1TEHAISAKI,N1.A1KOUNYUUTANKA,N1.A1KAHEI,N1.A1TEHAIBI,N1.A1NYUUKABI,N1.A1SHOUKEI,N1.A1KAKOTANKA, '' nullAlsa0,N1.A1CHUUBUNSHOMEISAINO,N1.A1CHUUBUNSHONO,N1.A1KIBOUTANKA,N1.A1NYUUKAYOTEIBI,N1.A1HINBANNO,N1.A1KOUJIKANRIBANGOU,N1.A1KAKUTEIKUBUN FROM (SELECT A1.CHUUBUNSHONO AS A1CHUUBUNSHONO ,A1.CHUUBUNSHOMEISAINO AS A1CHUUBUNSHOMEISAINO ,A1.HINBANNO AS A1HINBANNO ,A1.HINBANNO1 A1HINBANNO1 ,A1.HINMEI A1HINMEI ,A1.KOSUU A1KOSUU ,A1.SOUKOSUU A1SOUKOSUU ,A1.TSUKAMATSUNYUUSAKI A1TSUKAMATSUNYUUSAKI ,A1.KIBOUNOUKI A1KIBOUNOUKI ,A1.TEHAISAKI A1TEHAISAKI ,A1.KOUNYUUTANKA A1KOUNYUUTANKA ,A1.TEHAIBI A1TEHAIBI ,A1.NYUUKABI A1NYUUKABI ,A1.SHOUKEI A1SHOUKEI ,A1.KAKOTANKA A1KAKOTANKA ,A1.KIBOUTANKA A1KIBOUTANKA ,A1.NYUUKAYOTEIBI A1NYUUKAYOTEIBI ,A1.KAKUTEIKUBUN A1KAKUTEIKUBUN ,A1.KOUJIKANRIBANGOU A1KOUJIKANRIBANGOU ,A1.KAHEI A1KAHEI ,A1.SEISAKUHINKUBUN A1SEISAKUHINKUBUN ,A1.TANTOU A1TANTOU FROM `BUHINSYOUSAI` A1 WHERE 1=1 AND A1.CHUUBUNSHONO = #{BUHINCHUUBUNSHONO} AND A1.CHUUBUNSHOMEISAINO = #{BUHINCHUUBUNSHOMEISAINO} AND A1.KOUJIKANRIBANGOU = #{KOUJIKANRIBANGOU1} )N11=1 

AND   N1.A1NYUUKABI  >   #{KOUNYUUHIDZUKE1} 


AND   N1.A1CHUUBUNSHONO =  #{BUHINCHUUBUNSHONO} 


AND   N1.A1CHUUBUNSHOMEISAINO =  #{BUHINCHUUBUNSHOMEISAINO} 
 ORDER BY  N1.A1HINMEI asc, N1.A1TSUKAMATSUNYUUSAKI asc  LIMIT 50
